Network* SortingNetworks::getInstance(int nbWires) {
    if (nbWires >= static_cast<int>(INSTANCES.size())) return nullptr;
    auto* net = new Network(nbWires);
    net->parse(INSTANCES[nbWires]);
    return net;
}
